mapper.select:
SELECT
 COUNT(1)
FROM
  draw_cash t

    
      AND LOCATE(#{mobile, jdbcType=VARCHAR}, t.`mobile`) > 0
    
    
      AND  DATE_FORMAT(t.`create_time`, '%Y/%m/%d') >= #{applyTimeStart, jdbcType=VARCHAR}
    
    
      AND  DATE_FORMAT(t.`create_time`, '%Y/%m/%d') <= #{applyTimeEnd, jdbcType=VARCHAR}
    
    
      AND  DATE_FORMAT(t.`pay_time`, '%Y/%m/%d') >= #{payTimeStart, jdbcType=VARCHAR}
    
    
      AND  DATE_FORMAT(t.`pay_time`, '%Y/%m/%d') <= #{payTimeEnd, jdbcType=VARCHAR}
    
    
      AND LOCATE(#{alipayAccount, jdbcType=VARCHAR}, t.`alipay_account`) > 0
    
    
      AND t.status = #{status, jdbcType=VARCHAR}

SELECT
  t.*
FROM
  draw_cash t

    
      AND LOCATE(#{mobile, jdbcType=VARCHAR}, t.`mobile`) > 0
    
    
      AND  DATE_FORMAT(t.`create_time`, '%Y/%m/%d') >= #{applyTimeStart, jdbcType=VARCHAR}
    
    
      AND  DATE_FORMAT(t.`create_time`, '%Y/%m/%d') <= #{applyTimeEnd, jdbcType=VARCHAR}
    
    
      AND  DATE_FORMAT(t.`pay_time`, '%Y/%m/%d') >= #{payTimeStart, jdbcType=VARCHAR}
    
    
      AND  DATE_FORMAT(t.`pay_time`, '%Y/%m/%d') <= #{payTimeEnd, jdbcType=VARCHAR}
    
    
      AND LOCATE(#{alipayAccount, jdbcType=VARCHAR}, t.`alipay_account`) > 0
    
    
      AND t.status = #{status, jdbcType=VARCHAR}
    

ORDER BY t.create_time  DESC

SELECT
IFNULL(SUM(cash),0) cash
FROM
draw_cash t1

	
		and t1.`mobile` = #{mobile, jdbcType=VARCHAR}
	
	
		AND t1.`status` = #{status, jdbcType=INTEGER}
