mapper.update:

update user_order
set status2 = #{status2,jdbcType=INTEGER},
update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT
COUNT(1)
FROM
user_order t1

	
		and LOCATE(#{mobile, jdbcType=VARCHAR}, t1.`mobile`) > 0
	
	
		AND t1.`status1` = #{orderStatus, jdbcType=INTEGER}
	
	
		AND t1.`status2` = #{putForwardStatus, jdbcType=INTEGER}
	
	
		AND t1.`status3` = #{paymentStatus, jdbcType=INTEGER}
	
	
		AND DATE_FORMAT(t1.`create_time`,'%Y/%m/%d') >=#{startDate,
		jdbcType=VARCHAR}
	
	
		AND DATE_FORMAT(t1.`create_time`,'%Y/%m/%d') <=#{endDate,
		jdbcType=VARCHAR}

SELECT t1.id, t1.`order_id`, t1.`mobile`, t1.`create_time`, t1.`update_time`, IFNULL(t1.`product_info`,'') product_info, IFNULL(t1.`price`,'') price, IFNULL(t1.`rate`,'') rate, IFNULL(t1.`commission1`,'') commission1, IFNULL(t1.`commission3`,'') commission3, t1.`status1`, t1.`status2`, t1.`status3` FROM user_order t1
and LOCATE(#{mobile, jdbcType=VARCHAR}, t1.`mobile`) > 0
AND t1.`status1` = #{orderStatus, jdbcType=INTEGER}
AND t1.`status2` = #{putForwardStatus, jdbcType=INTEGER}
AND t1.`status3` = #{paymentStatus, jdbcType=INTEGER}
AND DATE_FORMAT(t1.`create_time`,'%Y/%m/%d') >=#{startDate, jdbcType=VARCHAR}
AND DATE_FORMAT(t1.`create_time`,'%Y/%m/%d') <=#{endDate, jdbcType=VARCHAR}
ORDER by t1.create_time DESC
